import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  FlatList
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState } from "react";
export default function CadastrarConta({ navigation }) {
  const [conta, setConta] = useState("");
  const [usuario, setUsuario] = useState("");
  const [senha, setSenha] = useState("");
  const [form, setForm] = useState([]);
  const [mostra, setMostra] = useState(false);

  function dados() {
    console.log(form);
    setMostra(True);
  }

  const busca = async () => {
    let val = await AsyncStorage.getItem("@ProjetoLogin:login");
    let dados = JSON.parse(val);
    setForm(dados);
  };

  const Lista = ({dado}) => {
    return(
      <View>
        <Text>{dado.user}</Text>
      </View>
    )
  }

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Cadastrar nova conta</Text>
        <View>
          <Text style={styles.txt}>Digite sua conta:</Text>
          <TextInput
            placeholder="Conta"
            style={styles.input}
            value={conta}
            onChangeText={setConta}
          />
          <Text style={styles.txt}>Digite seu usuário:</Text>
          <TextInput
            placeholder="Usuário"
            style={styles.input}
            value={usuario}
            onChangeText={setUsuario}
          />
          <Text style={styles.txt}>Digite sua senha:</Text>
          <TextInput
            placeholder="Senha"
            style={styles.input}
            value={senha}
            onChangeText={(e) => setSenha(e)}
          />
        </View>
        {mostra &&(
          <FlatList 
          data={form}
          keyExtractor={(item) => String(item)}
          renderItem ={({item}) => <Lista dado={{item}}/>}
        />
        )}
        <TouchableOpacity style={styles.btn} onPress={busca}>
          <Text>Cadastrar</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.btn} onPress={dados}>
          <Text>mostra</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#ffffff",
    borderWidth: 2,
    borderColor: "#2370eb",
    borderRadius: 10,
    padding: 10,
    minWidth: "60%",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "10%",
    margin: 10,
  },
});
