import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState, useEffect } from "react";
import CryptoES from 'crypto-es';

export default function CadastrarLogin({ navigation }) {
  const [form, setForm] = useState({
    user: "",
    password: "",
    data: [],
  });


  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  const cadastro = async () => {
    const encrypted = CryptoES.AES.encrypt("asd", "asd");
    console.log("encrypted: ", encrypted.toString());
    const decrypted = CryptoES.AES.decrypt(encrypted, "asd");
    console.log("decrypted: ", decrypted.toString(CryptoJS.enc.Utf8));
    
    await AsyncStorage.setItem("@ProjetoLogin:login", JSON.stringify(form));    
    alert("Cadastro realizado");
    navigation.navigate("Login");
  };

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Cadastre um login e senha</Text>
        <Text style={styles.txt}>Digite seu login:</Text>
        <TextInput
          placeholder="Login"
          style={styles.input}
          value={form.user}
          onChange={(e) => updateForm({ user: e.target.value })}
        />
        <Text style={styles.txt}>Digite sua senha:</Text>
        <TextInput
          placeholder="Senha"
          style={styles.input}
          value={form.password}
          onChange={(e) => updateForm({ password: e.target.value })}
        />
        <TouchableOpacity style={styles.btn} onPress={cadastro}>
          <Text>Cadastrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#ffffff",
    borderWidth: 2,
    borderColor: "#2370eb",
    borderRadius: 10,
    padding: 10,
    minWidth: "80%",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "20%",
    margin: 10,
  },
});
