import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState, useEffect } from "react";

export default function CadastrarConta({ navigation }) {
  const [form, setForm] = useState({
    conta: "",
    usuario: "",
    senha: "",
    data: new Date(),
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  const cadastro = async () => {
    try {
      const x = await AsyncStorage.multiSet([
        "@ProjetoLogin:contas",
        JSON.stringify(form),
      ]);

      console.log(JSON.stringify(x));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Cadastrar nova conta</Text>
        <Text style={styles.txt}>Digite sua conta:</Text>
        <TextInput
          placeholder="Conta"
          style={styles.input}
          value={form.conta}
          onChange={(e) => updateForm({ conta: e.target.value })}
        />
        <Text style={styles.txt}>Digite seu usuário:</Text>
        <TextInput
          placeholder="Usuário"
          style={styles.input}
          value={form.usuario}
          onChange={(e) => updateForm({ usuario: e.target.value })}
        />
        <Text style={styles.txt}>Digite sua senha:</Text>
        <TextInput
          placeholder="Senha"
          style={styles.input}
          value={form.senha}
          onChange={(e) => updateForm({ senha: e.target.value })}
        />
        <TouchableOpacity style={styles.btn} onPress={cadastro}>
          <Text>Cadastrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#ffffff",
    borderWidth: 2,
    borderColor: "#2370eb",
    borderRadius: 10,
    padding: 10,
    minWidth: "60%",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "10%",
    margin: 10,
  },
});
