import {
  StyleSheet,
  Text,
  View,
  Modal,
  TouchableOpacity,
  TextInput,
  FlatList,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import React, { useState } from "react";
import { useFocusEffect } from "@react-navigation/native";

export default function Home({ navigation }) {
  const [form, setForm] = useState({
    user: "",
    password: "",
    data: [],
  });
  const [contas, setContas] = useState("");
  const [usuarios, setUsuarios] = useState("");
  const [senhas, setSenhas] = useState("");
  const [isRender, setisRender] = useState(false);
  const [isModalVisible, setisModalVisible] = useState(false);
  const [editItem, seteditItem] = useState(false);

  useFocusEffect(
    React.useCallback(() => {
      getData();
    }, [])
  );

  const getData = async () => {
    try {
      const valor = await AsyncStorage.getItem("@ProjetoLogin:login");
      if (valor !== null) {
        const dados = JSON.parse(valor);
        setForm(dados);
      } else {
        alert("Erro na busca");
      }
    } catch (e) {
      console.log("Deu erro:" + e);
    }
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity style={styles.item} onPress={() => onPressItem(item)}>
      <Text style={{ color: "#000000", fontSize: 25 }}>
        {"Conta: " + item.conta}
      </Text>
      <Text style={{ color: "#000000", fontSize: 15 }}>
        {"Usuário: " + item.usuario}
      </Text>
      <Text style={{ color: "#000000", fontSize: 15 }}>
        {"Senha: " + item.senha}
      </Text>
      <TouchableOpacity onPress={() => ExcluirItem()}>
        <Text>Excluir</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );

  const onPressItem = (item) => {
    setisModalVisible(true);
    seteditItem(item.conta);
  };

  const SaveEdit = () => {
    handleEditItem(editItem);
    setisModalVisible(false);
  };

  const handleEditItem = (item) => {
    const newData = form.data.map((item) => {
      if (item.conta == editItem) {
        item.conta = contas;
        item.usuario = usuarios;
        item.senha = senhas;
        return item;
      }
      return item;
    });
    setForm((form.data = newData));
    setisRender(!isRender);
  };

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Contas salvas</Text>
        <FlatList
          style={styles.flatList}
          data={form.data}
          renderItem={renderItem}
          keyExtractor={(item) => item.conta}
          extraData={isRender}
        />
        <Modal
          animationType="fade"
          visible={isModalVisible}
          onRequestClose={() => setisModalVisible(false)}
        ></Modal>
        <TouchableOpacity
          style={styles.btn}
          onPress={() => navigation.navigate("Cadastrar conta")}
        >
          <Text>Cadastrar conta</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
    borderWidth: 2,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "10%",
    margin: 10,
  },
  flatList: {
    minWidth: "80%",
  },
  item: {
    backgroundColor: "#ffffff",
    padding: 10,
    marginVertical: 8,
    marginHorizontal: 16,
    borderRadius: 20,
    borderWidth: 2,
  },
});
