import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  FlatList,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState, useEffect } from "react";

export default function Home({ navigation }) {
  const [form, setForm] = useState({
    user: "",
    password: "",
    data: [
      {
        conta: "gmail",
        usuario: "aline",
        senha: "aline123",
      },
      {
        conta: "gmail2",
        usuario: "aline2",
        senha: "alinesdfsdf",
      },
    ],
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    try {
      const valor = await AsyncStorage.getItem("@ProjetoLogin:login");
      if (valor !== null) {
        const dados = JSON.parse(valor);
        setForm(dados);
      } else {
        console.log("Erro na busca");
      }
    } catch (e) {
      console.log("Deu erro:" + e);
    }
  };

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text>{title}</Text>
      <Text>{title.title}</Text>
      <Text>{title.senha}</Text>
    </View>
  );


  const renderItem = ({ item }) => <Item title={item.conta} />;

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Contas salvas</Text>
        <FlatList
          data={form.data}
          renderItem={renderItem}
          keyExtractor={(item) => item.conta}
        />
        <TouchableOpacity
          style={styles.btn}
          onPress={() => navigation.navigate("Cadastrar conta")}
        >
          <Text>Cadastrar conta</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#ffffff",
    borderWidth: 2,
    borderColor: "#2370eb",
    borderRadius: 10,
    padding: 10,
    minWidth: "60%",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "10%",
    margin: 10,
  },
});
