import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState, useEffect } from "react";

export default function Login() {
  const [form, setForm] = useState({
    id: "",
    nome: "",
    usuario: "",
    senha: "",
    data: "",
    mercado: "",
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }
  useEffect(() => {
    Cadastrar();
  },[]);

  async function Cadastrar() {
    await AsyncStorage.setItem("@ProjetoLogin:mercado", JSON.stringify(form));
    console.log(form);
  }

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={styles.titulo}>Efetue o login para continuar</Text>
        <Text style={styles.txt}>Digite seu login:</Text>
        <TextInput placeholder="Login" style={styles.input} value={form.usuario}  onChange={(e) => updateForm({ usuario: e.target.value })}/>
        <Text style={styles.txt}>Digite sua senha:</Text>
        <TextInput placeholder="Senha" style={styles.input}  value={form.senha} onChange={(e) => updateForm({ senha: e.target.value })}/>
        <TouchableOpacity style={styles.btn} onPress={Cadastrar}>
          <Text>Acessar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  container2: {
    flex: 1,
    backgroundColor: "#c0c1c2",
    alignItems: "center",
    justifyContent: "center",
    width: "90%",
    maxHeight: "90%",
    borderRadius: 10,
  },
  titulo: {
    fontSize: 25,
    margin: 10,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#ffffff",
    borderWidth: 2,
    borderColor: "#2370eb",
    borderRadius: 10,
    padding: 10,
    minWidth: "30%",
  },
  txt: {
    fontSize: 20,
    margin: 10,
  },
  btn: {
    alignItems: "center",
    backgroundColor: "#2370eb",
    borderWidth: 2,
    borderColor: "#000000",
    borderRadius: 10,
    padding: 10,
    minWidth: "10%",
    margin: 10,
  },
});
